import { NextApiRequest } from "next/types";
import { logger } from "@navikt/next-logger";
import { AuthUser } from "@/auth/auth.types";

export function getAuthUser(
  headers: NextApiRequest["headers"],
): AuthUser | null {
  const token = getToken(headers);
  if (!token) {
    return null;
  }

  const jwt = JSON.parse(
    Buffer.from(token.split(".")[1], "base64").toString("utf8"),
  );

  return {
    name: jwt.name,
    email: jwt.preferred_username,
  };
}

export const fakeToken =
  "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiIsImtpZCI6IjlHbW55RlBraGMzaE91UjIybXZTdmduTG83WSJ9..OOP_Q0ZcUI6jQzM1UON9mA8d8BU_GhioQ5OLuepwebg";

export function getToken(headers: NextApiRequest["headers"]) {
  if (process.env.NODE_ENV !== "production") {
    logger.info(
      "Is running locally or in demo, using local fake token for Authenticated user-requests",
    );
    return fakeToken;
  }

  return headers.authorization?.replace("Bearer ", "") ?? null;
}
