import * as React from 'react';
import Typografi from 'nav-frontend-typografi';
import EkspanderbartpanelBasePure from './ekspanderbartpanel-base-pure';

export interface EkspanderbartpanelPureProps {
    onClick: (event: React.SyntheticEvent<HTMLButtonElement>) => void;
    tittel: string;
    tittelProps: string;
    apen: boolean;
}

class EkspanderbartpanelPure extends React.PureComponent<EkspanderbartpanelPureProps> {

    render() {
        const { tittel, tittelProps, ...renderProps } = this.props;
        const heading = <Typografi type={tittelProps} tag="span" className="ekspanderbartPanel__heading">{tittel}</Typografi>;

        return (
            <EkspanderbartpanelBasePure {...renderProps} heading={heading} ariaTittel={tittel} />
        );
    }
}

export default EkspanderbartpanelPure;
