import React, { Component } from 'react';
import { PropTypes as PT } from 'prop-types';
import classNames from 'classnames';
import TypografiBase from 'nav-frontend-typografi';
import 'nav-frontend-etiketter-style';// eslint-disable-line import/extensions

const cls = (type, className) => classNames('etikett', className, {
    'etikett--advarsel': type === 'advarsel',
    'etikett--fokus': type === 'fokus',
    'etikett--suksess': type === 'suksess',
    'etikett--info': type === 'info'
});
/**
 * Etiketter finnes i fire predefinerte farger,
 * om det er behov anbefales å lage en egen klasse hvor man kan legge til flere farger
 */
class EtikettBase extends Component { // eslint-disable-line react/prefer-stateless-function
    render() {
        const { type, className, typo, children, ...props } = this.props;

        return (
            <div className={cls(type, className)} {...props}>
                <TypografiBase tag="span" type={typo}>{children}</TypografiBase>
            </div>
        );
    }
}

EtikettBase.propTypes = {
    /**
     * Typografi som skal brukes i etiketten. Støtter samme typer som `TypografiBase`. Påvirker ikke tag...
     */
    typo: PT.string,
    children: PT.node.isRequired,
    /**
     * Predefinerte farger
     */
    type: PT.oneOf(['advarsel', 'fokus', 'suksess', 'info']).isRequired,
    className: PT.string
};

EtikettBase.defaultProps = {
    typo: 'normaltekst',
    className: undefined
};

export default EtikettBase;

export { default as EtikettAdvarsel } from './etikettadvarsel';
export { default as EtikettFokus } from './etikettfokus';
export { default as EtikettSuksess } from './etikettsuksess';
export { default as EtikettInfo } from './etikettinfo';
