import PT from 'prop-types';
import React, { Component } from 'react';
import classNames from 'classnames';
import 'nav-frontend-grid-style';// eslint-disable-line import/extensions

const cls = (fluid, className) => classNames(className, {
    container: fluid === false,
    'container-fluid': fluid === true
});

class Container extends Component { // eslint-disable-line react/prefer-stateless-function
    render() {
        const { children, className, fluid, ...props } = this.props;
        return (
            <div className={cls(fluid, className)} {...props}>{children}</div>
        );
    }
}

Container.defaultProps = {
    className: undefined,
    children: undefined,
    fluid: false
};

Container.propTypes = {
    className: PT.string,
    fluid: PT.bool,
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ])
};

export default Container;
