import PT from 'prop-types';
import React, { Component } from 'react';
import classNames from 'classnames';
import 'nav-frontend-grid-style'; // eslint-disable-line import/extensions

const cls = (className) => classNames('row', className, {});

class Row extends Component { // eslint-disable-line react/prefer-stateless-function
    render() {
        const { children, className, ...props } = this.props;

        return (
            <div className={cls(className)} {...props}>{children}</div>
        );
    }
}

Row.defaultProps = {
    className: undefined,
    children: undefined
};

Row.propTypes = {
    className: PT.string,
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ])
};

export default Row;
