import React, { Component } from 'react';
import 'nav-frontend-inngang-style'; // eslint-disable-line import/extensions
import PT from 'prop-types';
import { Innholdstittel } from 'nav-frontend-typografi';
import { HoyreChevron } from 'nav-frontend-chevron';

class AppInngang extends Component { // eslint-disable-line react/prefer-stateless-function

    render() {
        const { tittel, ...props } = this.props;
        return (
            <a {... props} className="inngang inngang__app">
                <Innholdstittel>{ tittel }</Innholdstittel>
                <HoyreChevron stor />
            </a>
        );
    }
}

AppInngang.propTypes = {
    tittel: PT.string.isRequired
};

export default AppInngang;
