import React, { Component } from 'react';
import PT from 'prop-types';
import { Innholdstittel, Ingress } from 'nav-frontend-typografi';
import { HoyreChevron } from 'nav-frontend-chevron';
import Ikon from 'nav-frontend-ikoner-assets'; // eslint-disable-line import/extensions
import 'nav-frontend-inngang-style'; // eslint-disable-line import/extensions

class EnkelIngressInngang extends Component { // eslint-disable-line react/prefer-stateless-function
    render() {
        const { ingress, tittel, ikon, ...props } = this.props;

        return (
            <a {... props} className="inngang inngang__enkelingress">
                <div className="inngang__enkelingress__content">
                    <div className="ikonWrapper">
                        <Ikon kind={ikon} size={80} />
                    </div>
                    <div className="textWrapper">
                        <Innholdstittel>{ tittel }</Innholdstittel>
                        <Ingress>{ ingress }</Ingress>
                    </div>
                </div>
                <HoyreChevron stor />
            </a>
        );
    }
}

EnkelIngressInngang.propTypes = {
    tittel: PT.string.isRequired,
    ingress: PT.string.isRequired,
    ikon: PT.string.isRequired
};

export default EnkelIngressInngang;
