import * as React from 'react';
import KnappBase from './index';
import {CustomHTMLButtonAttributes} from "./CustomHTMLButtonAttributes";

export interface KnappProps extends CustomHTMLButtonAttributes {
    htmlType?: 'submit' | 'button' | 'reset',
    mini?: boolean,
    spinner?: boolean,
    autoDisableVedSpinner?: boolean,
    inaktivKlasseVedDisabled?: boolean
}

class Knapp extends React.Component<KnappProps> { // eslint-disable-line react/prefer-stateless-function
    render() {
        return <KnappBase {...this.props} type="standard" />;
    }
}

export default Knapp;
