import PT from 'prop-types';
import React, { Component } from 'react';
import classnames from 'classnames';
import 'nav-frontend-lenkepanel-style'; // eslint-disable-line import/extensions

const cls = (className) => classnames('lenkepanel', className);

/**
 * Lenkepanelinnhold
 */
class LenkepanelInnhold extends Component {
    render() {
        const { linkCreator, ...renderProps } = this.props;

        return linkCreator({
            ...renderProps,
            className: cls(renderProps.className)
        });
    }
}

LenkepanelInnhold.propTypes = {
    /**
     * Klassenavn
     */
    className: PT.string,
    /**
     * Lenke (denne sendes som prop til linkCreator)
     */
    href: PT.string.isRequired,
    /**
     * Innhold for lenkepanelinnhold
     */
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ]).isRequired,
    /**
     * Funksjon som brukes for Ã¥ lage lenken. her kan du spesifisere din egen hvis du f.eks. bruker React Router
     */
    linkCreator: PT.func
};

LenkepanelInnhold.defaultProps = {
    className: undefined,
    linkCreator: (props) => <a {...props} /> // eslint-disable-line jsx-a11y/anchor-has-content
};

export default LenkepanelInnhold;
