import React from 'react';
import { storiesOf } from '@storybook/react';
import { JSDokumentasjon } from './../../dokumentasjon';
import readme from './README.md';
import pkg from './package.json';
import Lenkepanel from './src/index';

function customLinkCreator({ children, ...props }) { // eslint-disable-line react/prop-types
    return (
        <a {...props} style={{ backgroundColor: 'cyan' }} >
            {children}
        </a>);
}

storiesOf('Paneler', module)
    .addWithSections('Lenkepanel', () => (
        <div style={{ backgroundColor: '#efefef', padding: '1rem' }}>
            <Lenkepanel tittelProps="innholdstittel" href="#">Lenkepanel</Lenkepanel>
            <Lenkepanel tittelProps="undertittel" href="#">Lenkepanel</Lenkepanel>
            <Lenkepanel tittelProps="systemtittel" href="#">Lenkepanel</Lenkepanel>
            <Lenkepanel tittelProps="element" href="#">Lenkepanel</Lenkepanel>
            <Lenkepanel tittelProps="element" href="#" linkCreator={customLinkCreator}>Lenkepanel</Lenkepanel>
        </div>
    ), JSDokumentasjon(pkg, readme, Lenkepanel))
    .add('Lenkepanel med egendefinert inhold', () => (
        <div style={{ backgroundColor: '#efefef', padding: '1rem' }}>
            <Lenkepanel href="#"><div><h3>egendefinert inhold</h3></div></Lenkepanel>
            <Lenkepanel href="#" linkCreator={customLinkCreator}><div><h3>egendefinert inhold</h3></div></Lenkepanel>
        </div>
    ));
