import React from 'react';
import { storiesOf } from '@storybook/react';
import { JSDokumentasjon } from './../../dokumentasjon';
import readme from './README.md';
import pkg from './package.json';
import Lenkepanel from './src/lenkepanel';
import LenkepanelInnhold from './src/lenkepanelinnhold';

function customLinkCreator({ children, ...props }) { // eslint-disable-line react/prop-types
    return (
        <a {...props} style={{ backgroundColor: 'red', color: 'white' }}>
            { children }
        </a>
    );
}

storiesOf('Paneler', module)
    .addWithSections('Lenkepanel', () => (
        <section style={{ backgroundColor: '#efefef', padding: '1rem' }}>
            <Lenkepanel href="#" tittelProps="undertittel">
                Lenkepanel med kun tekst som typo-undertittel
            </Lenkepanel>
            <Lenkepanel href="#" tittelProps="systemtittel" linkCreator={customLinkCreator}>
                Lenkepanel med kun tekst som typo-systemtittel, og med customLinkCreator som endrer styling.
            </Lenkepanel>
            <LenkepanelInnhold href="#">
                <h1 className="typo-innholdstittel">Lenkepanelinnhold med overskrift...</h1>
                <p>...og flere elementer</p>
            </LenkepanelInnhold>
        </section>
    ), JSDokumentasjon(pkg, readme, Lenkepanel));
