import PT from 'prop-types';
import * as React from 'react';
import * as classNames from 'classnames';
import 'nav-frontend-lukknapp-style'; // eslint-disable-line import/extensions

const cls = (bla, hvit, hjorne, className) => classNames('lukknapp',
    {
        'lukknapp--hvit': hvit,
        'lukknapp--bla': bla,
        'lukknapp--overstHjorne': hjorne
    },
    className
);

export interface LukknappProps {
    children: PT.string,
    bla?: boolean,
    hvit?: boolean,
    overstHjorne?: boolean,
    className?: string,
    ariaLabel: string
}

/**
 * Lukknapp
 */
class Lukknapp extends React.Component<LukknappProps, {}> {
    buttonRef: HTMLButtonElement;

    constructor(props) {
        super(props);
        this.focus = this.focus.bind(this);
    }

    focus() {
        if (this.buttonRef) {
            this.buttonRef.focus();
        }
    }

    render() {
        const { children, bla, hvit, overstHjorne, className, ariaLabel, ...props } = this.props;
        return (
            <button
                ref={(buttonRef: HTMLButtonElement) => { this.buttonRef = buttonRef; }}
                className={cls(bla, hvit, overstHjorne, className)}
                aria-label={ariaLabel}
                {...props}
            >
                <span className="text-hide">{children}</span>
            </button>
        );
    }
}

(Lukknapp as any).defaultProps = {
    children: 'Lukk',
    bla: false,
    hvit: false,
    overstHjorne: false,
    className: '',
    ariaLabel: 'Lukk'
};

(Lukknapp as any).propTypes = {
    /**
     * Tekst som beskriver knappen for skjermleser
     */
    children: PT.string,
    /**
     * Bruk blå som standard farge i stedet for grå
     */
    bla: PT.bool,
    hvit: PT.bool,
    /**
     * Plasser knappen øverst i høyre hjørne.
     * Knappen er absolut possisjonert og trenger derfor litt hjelp med å legge seg riktig sted.
     */
    overstHjorne: PT.bool,
    className: PT.string,
    ariaLabel: PT.string.isRequired
};

export default Lukknapp;
