import PT from 'prop-types';
import React from 'react';
import classNames from 'classnames';
import 'nav-frontend-paneler-style'; // eslint-disable-line import/extensions

export const cls = (className) => classNames('panelRad', className);

function PanelRadBase({ children, className, ...props }) {
    return (
        <div className={cls(className)} {...props}>{children}</div>
    );
}

PanelRadBase.propTypes = {
    className: PT.string,
    children: PT.node.isRequired
};
PanelRadBase.defaultProps = {
    className: undefined
};

export default PanelRadBase;

export const PanelRad = (props) => <PanelRadBase {...props} />;
