import PT from 'prop-types';
/* eslint-disable react/prefer-stateless-function  */
import React, { Component } from 'react';
import 'nav-frontend-skjema-style'; // eslint-disable-line import/extensions
import classNames from 'classnames';

const cls = (className) => classNames('skjema__fieldset', className);

/**
 * Fieldset
 */
class Fieldset extends Component {
    render() {
        const { children, className, legend, ...other } = this.props;
        return (
            <fieldset className={cls(className)} {...other}>
                <legend className="skjema__legend">{legend}</legend>
                {children}
            </fieldset>
        );
    }
}

Fieldset.propTypes = {
    /**
     * En eller flere children, oftes .skjemaelement__sporsmal eller .skjemaelement
     */
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ]).isRequired,
    /**
     * ClassName
     */
    className: PT.string,
    /**
     * legend for fieldset
     */
    legend: PT.string.isRequired
};

Fieldset.defaultProps = {
    className: undefined
};

export default Fieldset;
