import * as React from 'react';
import Input, { InputProps } from './input';
import { fnr } from '@navikt/fnrvalidator';
import 'nav-frontend-skjema-style';

export interface FnrInputProps extends InputProps {
    onValidate: (isValid: boolean) => void;
}

class FnrInput extends React.Component<FnrInputProps> {
    onChange(e) {
        if (this.props.onValidate) {
            const valid = e.currentTarget.validity.valid && fnr(e.currentTarget.value).status === 'valid';
            this.props.onValidate(valid);
        }
        if (this.props.onChange) {
            this.props.onChange(e);
        }
    }
    render () {
        return (
            <Input
                {...this.props}
                type="number"
                inputClassName="skjemaelement__input_fodselsnr"
                onChange={(e) => this.onChange(e)}
            />
        );
    }
}

export default FnrInput;
