import PT from 'prop-types';
/* eslint-disable react/prefer-stateless-function  */
import React, { Component } from 'react';
import 'nav-frontend-skjema-style'; // eslint-disable-line import/extensions
import classNames from 'classnames';

const cls = (className, harFeil) => classNames(className, {
    'skjema--harFeil': harFeil
});

const clsFeilomrade = (harFeil) => classNames({
    skjema__feilomrade: harFeil
});

/**
 * Fieldset
 */
class SkjemaGruppe extends Component {
    render() {
        const { children, className, title, feil, ...other } = this.props;
        return (
            <div className={cls(className, !!feil)} {...other}>
                <div className={clsFeilomrade(!!feil)}>
                    <div className="skjemaelement__sporsmal">{title}</div>
                    {children}
                    <div role="alert" aria-live="assertive" className="skjemaelement__feilmelding">
                        {feil ? feil.feilmelding : null}
                    </div>
                </div>
            </div>
        );
    }
}

SkjemaGruppe.propTypes = {
    /**
     * En eller flere children, oftest en eller flere .skjemaelement
     */
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ]).isRequired,
    /**
     * ClassName
     */
    className: PT.string,
    /**
     * Tittel eller skjemaspørsmål for skjemagruppe
     */
    title: PT.string.isRequired,
    /**
     * Hvis skjemagruppen har feil sender man inn et objekt med en feilmelding
     */
    feil: PT.shape({
        feilmelding: PT.string.isRequired
    })
};

SkjemaGruppe.defaultProps = {
    className: undefined,
    feil: undefined
};

export default SkjemaGruppe;
