class ControlledTextareas extends React.Component {

    render() {
        const state = this.state || { text: 'Initiell verdi.' };
        const handleChange = (e) => {
            this.setState({ text: e.target.value });
        };
        return (
            <div>
                <Textarea
                    label="Controlled Textarea 1"
                    onChange={handleChange}
                    value={state.text}
                />

                <Textarea
                    label="Controlled Textarea 2"
                    onChange={handleChange}
                    value={state.text}
                />
            </div>
        );
    }
}

