import PT from 'prop-types';
import React, { Component } from 'react';
import Ikoner from 'nav-frontend-ikoner-assets';
import classNames from 'classnames';
import 'nav-frontend-spinner-style';// eslint-disable-line import/extensions

const cls = (className, storrelse) => classNames('spinner', className, {
    [`spinner--${storrelse.toLowerCase()}`]: !!storrelse
});

class NavFrontendSpinner extends Component { // eslint-disable-line react/prefer-stateless-function
    render() {
        const { negativ, stroke, type, className, ...props } = this.props;

        const ikon = stroke ? 'spinner-stroke' : 'spinner';
        const ikonNegativ = negativ ? `${ikon}-negativ` : ikon;

        return (
            <Ikoner kind={ikonNegativ} className={cls(className, type)} {...props} />
        );
    }
}

NavFrontendSpinner.propTypes = {
    negativ: PT.bool,
    stroke: PT.bool,
    type: PT.oneOf(['XXS', 'XS', 'S', 'M', 'L', 'XL', 'XXL', 'XXXL']),
    className: PT.string,
    'aria-label': PT.string // eslint-disable-line react/require-default-props
};

NavFrontendSpinner.defaultProps = {
    'aria-label': 'Laster innhold',
    negativ: false,
    stroke: false,
    type: 'm',
    className: undefined
};

export default NavFrontendSpinner;
