import React from 'react';
import { storiesOf } from '@kadira/storybook';
import { JSDokumentasjon } from './../../dokumentasjon';
import readme from './README.md';
import pkg from './package.json';
import Tekstomrade from './src/tekstomrade';

const tekst = 'Tekst med lenker til www.google.com og https://www.nav.no\nsamt\r\nflere\navsnitt';

storiesOf('Tekstomrade', module)
    .addWithSections('Default', () => (
        <div>
            <h3>Formattert tekst:</h3>
            <Tekstomrade>{tekst}</Tekstomrade>

            <h3>Uformattert tekst:</h3>
            <Tekstomrade ingenFormattering>{tekst}</Tekstomrade>

            <h3>Med custom props</h3>
            <Tekstomrade
                className="typo-sidetittel"
                style={{ backgroundColor: '#ddd' }}
            >{tekst}</Tekstomrade>
        </div>
    ), JSDokumentasjon(pkg, readme, Tekstomrade));
