import React, { Component } from 'react';
import { storiesOf } from '@storybook/react';
import { JSDokumentasjon } from './../../dokumentasjon';
import readme from './README.md';
import pkg from './package.json';
import { ToggleGruppe, ToggleKnapp } from './src/toggle';

class ControlledToggleGruppe extends Component {
    constructor(props) {
        super(props);

        this.state = { gruppe1: 'knapp-1' };

        this.onChange = this.onChange.bind(this);
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value });
    }

    render() {
        return (
            <ToggleGruppe name="gruppe1" onChange={this.onChange} className="blokk-m">
                <ToggleKnapp value="knapp-1" checked={this.state.gruppe1 === 'knapp-1'}>Knapp en</ToggleKnapp>
                <ToggleKnapp value="knapp-2" checked={this.state.gruppe1 === 'knapp-2'}>Knapp to</ToggleKnapp>
            </ToggleGruppe>
        );
    }
}


storiesOf('Toggle', module)
    .addWithSections('Default', () => (
        <div>
            <ControlledToggleGruppe />
            <ToggleGruppe name="gruppe2" className="blokk-m">
                <ToggleKnapp value="knapp-1">Knapp en</ToggleKnapp>
                <ToggleKnapp value="knapp-2" defaultChecked>Knapp to</ToggleKnapp>
                <ToggleKnapp value="knapp-3">Knapp tre</ToggleKnapp>
                <ToggleKnapp value="knapp-4">Knapp fire</ToggleKnapp>
            </ToggleGruppe>
            <ToggleGruppe name="gruppe3" className="blokk-m">
                <ToggleKnapp value="knapp-1">Knapp en</ToggleKnapp>
                <ToggleKnapp value="knapp-2">Knapp to</ToggleKnapp>
                <ToggleKnapp value="knapp-3" defaultChecked>Knapp tre</ToggleKnapp>
                <ToggleKnapp value="knapp-4">Knapp fire</ToggleKnapp>
            </ToggleGruppe>
        </div>
    ), JSDokumentasjon(pkg, readme, ToggleGruppe));
