              selected={selected.includes(option)}
              key={option}
              onClick={() =>
                setSelected(
                  selected.includes(option)
                    ? selected.filter((x) => x !== option)
                    : [...selected, option],
                )
              }
            >
              {option}
            </Chips.Toggle>
          ))}
        </Chips>
        <Chips size="small">
          {filter.map((option) => (
            <Chips.Removable
              key={option}
              onClick={() =>
                setFilter((x) =>
                  x.length === 1 ? options : x.filter((y) => y !== option),
                )
              }
            >
              {option}
            </Chips.Removable>
          ))}
        </Chips>
      </VStack>
    </VStack>
  );
};

