import { MonthPicker } from "@navikt/ds-react";

const year = new Date().getFullYear();

const disabledDays = [
  new Date(`May 10 ${year}`),
  { from: new Date(`Nov 17 ${year}`), to: new Date(`Feb 10 ${year + 1}`) },
];

const Example = () => {
  return (
    <MonthPicker.Standalone
      onMonthSelect={console.info}
      dropdownCaption
      fromDate={new Date(`1 Oct ${year - 2}`)}
      toDate={new Date(`1 Oct ${year + 2}`)}
      disabled={disabledDays}
    />
  );
};

import { MonthPicker, VStack, useMonthpicker } from "@navikt/ds-react";

const Example = () => {
  const { monthpickerProps, inputProps, selectedMonth } = useMonthpicker({
    fromDate: new Date("Aug 23 2019"),
    toDate: new Date("Aug 23 2025"),
    onMonthChange: console.info,
  });

  return (
    <VStack gap="space-16" minHeight="24rem">
      <MonthPicker {...monthpickerProps}>
        <MonthPicker.Input {...inputProps} label="Velg måned" />
      </MonthPicker>
      {selectedMonth?.toLocaleDateString(undefined, {
        year: "numeric",
        month: "2-digit",
      })}
    </VStack>
  );
};

import { useState } from "react";
import { Button, MonthPicker, VStack } from "@navikt/ds-react";

const Example = () => {
  const [month, setMonth] = useState<Date>();
  const [open, setOpen] = useState(false);

  return (
    <VStack gap="space-16" minHeight="16rem">
      <MonthPicker
        onMonthSelect={setMonth}
        onClose={() => setOpen(false)}
        open={open}
