import React, { useEffect } from "react";
import {
  Button,
  ErrorSummary,
  Heading,
  Page,
  Radio,
  RadioGroup,
  TextField,
  VStack,
  HGrid,
  Hide,
  Show,
} from "@navikt/ds-react";
import { ArrowLeftIcon, ArrowRightIcon } from "@navikt/aksel-icons";

const validateFødselsnummer = (p: string) => {
  // Det er anbefalt å bruke https://github.com/navikt/fnrvalidator for å validere fødselsnummer.
  if (p.length === 0) {
    return "Du må fylle ut fødselsnummer.";
  }
  if (!/^\d{11}$/.test(p)) {
    return "Fødselsnummer må være 11 siffer.";
  }
  return "";
};

const Example = () => {
  const errorSummaryRef = React.useRef<HTMLDivElement>(null);

  const [formState, setFormState] = React.useState({
    submitted: false,
    tries: 0,
  });
  const [values, setValues] = React.useState({
    fødselsnummer: "",
    transportmiddel: "",
  });
  const [errors, setErrors] = React.useState({
    fødselsnummer: "",
    transportmiddel: "",
  });

  function onSubmit(event: React.FormEvent) {
    event.preventDefault();

    const newErrors = {
      fødselsnummer: validateFødselsnummer(values.fødselsnummer),
      transportmiddel: values.transportmiddel
        ? ""
        : "Du må velge et transportmiddel.",
    };
    setErrors(newErrors);

    if (Object.values(newErrors).some(Boolean)) {
      setFormState({ ...formState, tries: formState.tries + 1 });
    } else {
      setFormState({ ...formState, submitted: true });
    }
  }

  useEffect(() => {
    formState.tries > 0 && errorSummaryRef.current?.focus();
  }, [formState.tries]);

  if (formState.submitted)
    return (
      <Page.Block width="lg" gutters>
        <VStack gap="8" align="center">
          <Heading size="large">Demo slutt</Heading>
          <Button
            onClick={() => {
              location.hash = "";
              location.reload();
            }}
          >
            Nullstill
          </Button>
        </VStack>
      </Page.Block>
    );

  return (
    <Page.Block width="lg" gutters>
      <form onSubmit={onSubmit}>
        <VStack gap="8">
          <TextField
            id="fødselsnummer"
            label="Fødselsnummer"
            htmlSize={11}
            value={values.fødselsnummer}
            onChange={(e) =>
              setValues({ ...values, fødselsnummer: e.currentTarget.value })
            }
            onBlur={() => {
              formState.tries &&
                setErrors({
                  ...errors,
                  fødselsnummer: validateFødselsnummer(values.fødselsnummer),
                });
            }}
            error={errors.fødselsnummer}
          />
          <RadioGroup
            legend="Transportmiddel"
            value={values.transportmiddel}
            onChange={(newValue) => {
              setValues({ ...values, transportmiddel: newValue });
              setErrors({ ...errors, transportmiddel: "" });
            }}
            error={errors.transportmiddel}
          >
            <Radio value="car" id="transportmiddel">
              Bil
            </Radio>
            <Radio value="walking">Gange</Radio>
            <Radio value="public">Kollektivtransport</Radio>
          </RadioGroup>

          {Object.values(errors).some(Boolean) && (
            <ErrorSummary
              ref={errorSummaryRef}
              heading="Du må rette disse feilene før du kan fortsette:"
            >
              {Object.entries(errors)
                .filter(([, error]) => error)
                .map(([key, error]) => (
                  <ErrorSummary.Item href={`#${key}`} key={key}>
                    {error}
                  </ErrorSummary.Item>
                ))}
            </ErrorSummary>
          )}

          <HGrid
            gap={{ xs: "space-16", sm: "space-32 space-16" }}
            columns={{ xs: 1, sm: 2 }}
            width={{ sm: "fit-content" }}
          >
            <Hide above="sm" asChild>
              <Button
                variant="primary"
                icon={<ArrowRightIcon aria-hidden />}
                iconPosition="right"
              >
                Neste steg
              </Button>
            </Hide>
            <Button
              type="button"
              variant="secondary"
              icon={<ArrowLeftIcon aria-hidden />}
              iconPosition="left"
            >
              Forrige steg
            </Button>
            <Show above="sm" asChild>
              <Button
                variant="primary"
                icon={<ArrowRightIcon aria-hidden />}
                iconPosition="right"
              >
                Neste steg
              </Button>
            </Show>
          </HGrid>
        </VStack>
      </form>
    </Page.Block>
  );
};

import React from "react";
import { SubmitHandler, useForm } from "react-hook-form";
import {
  Button,
  ErrorSummary,
  Heading,
  Page,
  Radio,
  RadioGroup,
  TextField,
  VStack,
  HGrid,
  Hide,
  Show,
} from "@navikt/ds-react";
import { ArrowLeftIcon, ArrowRightIcon } from "@navikt/aksel-icons";

type Inputs = {
  fødselsnummer: string;
  transportmiddel: string;
};

const Example = () => {
  const errorSummaryRef = React.useRef<HTMLDivElement>(null);

  const {
    register,
    handleSubmit,
    trigger,
    formState: { isSubmitSuccessful, errors },
  } = useForm<Inputs>({
    reValidateMode: "onBlur",
    shouldFocusError: false,
  });

  const onValidSubmit: SubmitHandler<Inputs> = (data) => {
    console.info("data", data);
  };

  if (isSubmitSuccessful)
    return (
      <Page.Block width="lg" gutters>
        <VStack gap="8" align="center">
          <Heading size="large">Demo slutt</Heading>
          <Button
            onClick={() => {
              location.hash = "";
              location.reload();
            }}
          >
            Nullstill
          </Button>
        </VStack>
      </Page.Block>
    );

  return (
    <Page.Block width="lg" gutters>
      <form
        onSubmit={(event) => {
          handleSubmit(onValidSubmit)(event).then(() => {
            errorSummaryRef.current?.focus();
          });
        }}
      >
        <VStack gap="8">
          <TextField
            id="fødselsnummer"
            label="Fødselsnummer"
            htmlSize={11}
            error={errors.fødselsnummer?.message}
            {...register("fødselsnummer", {
              required: "Du må fylle ut fødselsnummer.",
              pattern: {
                value: /^\d{11}$/,
                message: "Fødselsnummer må være 11 siffer.",
              }, // Det er anbefalt å bruke https://github.com/navikt/fnrvalidator for å validere fødselsnummer.
            })}
          />
          <RadioGroup
            legend="Transportmiddel"
            error={errors.transportmiddel?.message}
            onChange={() => trigger("transportmiddel")}
            name="transportmiddel"
          >
            {["Bil", "Gange", "Kollektivtransport"].map((value) => (
              <Radio
                key={value}
                id={value === "Bil" ? "transportmiddel" : undefined}
                value={value}
                {...register("transportmiddel", {
                  required: "Du må velge et transportmiddel.",
                })}
              >
                {value}
              </Radio>
            ))}
          </RadioGroup>

          {Object.values(errors).length > 0 && (
            <ErrorSummary
              ref={errorSummaryRef}
              heading="Du må rette disse feilene før du kan fortsette:"
            >
              {Object.entries(errors).map(([key, error]) => (
                <ErrorSummary.Item key={key} href={`#${key}`}>
                  {error.message}
                </ErrorSummary.Item>
              ))}
            </ErrorSummary>
          )}

          <HGrid
            gap={{ xs: "space-16", sm: "space-32 space-16" }}
            columns={{ xs: 1, sm: 2 }}
            width={{ sm: "fit-content" }}
          >
            <Hide above="sm" asChild>
              <Button
                variant="primary"
                icon={<ArrowRightIcon aria-hidden />}
                iconPosition="right"
              >
                Neste steg
              </Button>
            </Hide>
            <Button
              type="button"
              variant="secondary"
              icon={<ArrowLeftIcon aria-hidden />}
              iconPosition="left"
            >
              Forrige steg
            </Button>
            <Show above="sm" asChild>
              <Button
                variant="primary"
                icon={<ArrowRightIcon aria-hidden />}
                iconPosition="right"
              >
                Neste steg
              </Button>
            </Show>
          </HGrid>
        </VStack>
      </form>
    </Page.Block>
  );
};

