import { useState } from "react";
import {
  HospitalIcon,
  ParasolBeachIcon,
  PencilIcon,
  PersonIcon,
  PiggybankIcon,
  VirusIcon,
} from "@navikt/aksel-icons";
import { Box, Timeline, TimelinePeriodProps } from "@navikt/ds-react";

const Example = () => {
  const [activePeriod, setActivePeriod] = useState("");

  return (
    <Box marginInline="auto" maxWidth="800px">
      <Timeline>
        <Timeline.Row label="Person" icon={<PersonIcon aria-hidden />}>
          {person.map((p, i) => (
            <Timeline.Period
              key={i}
              start={p.start}
              end={p.end}
              status={p.status}
              icon={p.icon}
              statusLabel={p.statusLabel}
              onSelectPeriod={() => setActivePeriod(p.id)}
              isActive={activePeriod === p.id}
              aria-controls="timeline-panel"
              id={p.id}
            />
          ))}
        </Timeline.Row>
        <Timeline.Row label="Sykehus A" icon={<HospitalIcon aria-hidden />}>
          {jobb.map((p, i) => (
            <Timeline.Period
              key={i}
              start={p.start}
              end={p.end}
              status={p.status}
              icon={p.icon}
              statusLabel={p.statusLabel}
              onSelectPeriod={() => setActivePeriod(p.id)}
              isActive={activePeriod === p.id}
              aria-controls="timeline-panel"
              id={p.id}
            />
          ))}
        </Timeline.Row>
      </Timeline>
      {activePeriod && (
        <Box
          aria-controls={activePeriod}
          id="timeline-panel"
          marginBlock="space-32 space-0"
        >{`${activePeriod}: ${[...person, ...jobb].find(
          (p) => p.id === activePeriod,
        )?.start}`}</Box>
      )}
    </Box>
  );
};

